import React, {useEffect, useState} from 'react';
import { getHistory } from '../api';

export default function MigrationsTimeline({env='dev'}){
  const [history, setHistory] = useState([]);

  async function load(){ try{ const r = await getHistory(env); setHistory(r.history || []); }catch(e){ console.error(e); } }

  useEffect(()=>{ load(); }, [env]);

  return (
    <div className="card" style={{maxHeight:400, overflow:'auto'}}>
      <ul>
        {history.map(h => (
          <li key={h.orderexecuted} style={{padding:8, borderBottom:'1px solid #eee'}}>
            <div><strong>{h.id}</strong> â€” {h.description || h.filename}</div>
            <div style={{fontSize:12, color:'#666'}}>by {h.author} at {h.dateexecuted}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}
