import React, {useEffect, useState} from 'react';
import { getPending, executeMigration } from '../api';

export default function PendingMigrations({env='dev'}){
  const [pending, setPending] = useState([]);
  const [running, setRunning] = useState(false);

  async function load(){ try{ const res = await getPending(env); setPending(res.pending || []); }catch(e){ console.error(e); } }

  useEffect(()=>{ load(); }, [env]);

  async function runOne(fn){
    if(!fn) return;
    if(!confirm('Execute migration '+fn+' on '+env+'?')) return;
    setRunning(true);
    try{
      const r = await executeMigration({env, changelogFile: fn});
      alert('Output:\n'+(r.output || 'OK'));
      load();
    }catch(e){ alert('Error: '+ (e.response?.data?.error || e.message)); }
    setRunning(false);
  }

  return (
    <div className="card">
      {pending.length === 0 ? <div>No pending</div> :
        <ul>
          {pending.map(p => (
            <li key={p.id} style={{marginBottom:8}}>
              <div><strong>{p.id}</strong> â€” {p.description}</div>
              <div style={{marginTop:6}}><button onClick={()=>runOne(p.filename)} disabled={running}>Run</button></div>
            </li>
          ))}
        </ul>
      }
    </div>
  );
}
